################################################################################
# CATEGORY
#   AGENT CONTROL FUNCTIONS
#
# NAME
#   ff_agent_update_install - Generate an update script for ff_agent that reinstalls and restarts it via pm2.
#
# SYNOPSIS
#   ff_agent_update_install
#
# DESCRIPTION
#   Ensures that the ff_agent binary is available in the PATH, then generates a
#   reusable update script (`ff_agent_update`) under the ${FF_AGENT_HOME}/bin directory.
#   This script, when executed, performs the following actions:
#
#     - Verifies environment integrity using set_environment_is_working.
#     - Installs a specific version of @ff/ff_agent globally using npm.
#     - Outputs the version of the installed package.
#     - Flushes the pm2 logs via ff_agent_logs_flush script.
#     - Restarts the ff_agent process via ff_agent_restart script.
#
#   The generated script includes error handling and state setting for each step.
#   After the script is created, this function ensures it is marked as executable.
#
# DEPENDENCIES
#   - command_exists
#   - state_set
#   - abort
#   - ensure_variables_not_empty
#   - set_environment_is_working (runtime in generated script)
#   - npm
#   - pm2
#   - chmod
#   - bash (must support here-docs)
#
# USAGE EXAMPLE
#   ff_agent_update_install
#
# EXIT STATUS
#   Sets state and aborts if ff_agent is not found in PATH.
#   Sets state and returns non-zero on failure to create or chmod the script.
#   Returns 0 on successful script generation and setup.
#
################################################################################
function ff_agent_update_install {
  state_set "${FUNCNAME[0]}" 'started'

  # Check dependencies
  local DEPENDENCIES=("ff_agent")
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # Validate required non-empty variables
  local REQUIRED_NON_EMPTY_VARIABLES=("FF_AGENT_HOME")
  ensure_variables_not_empty "${FUNCNAME[0]}" "${REQUIRED_NON_EMPTY_VARIABLES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'ensure_variables_not_empty_failed'
    return 1
  }

  # Create update script target
  local TARGET_FILE="${FF_AGENT_HOME}/bin/ff_agent_update"

  # Generate ff_agent_update script via here-doc
  (
  # Note: this here-doc will be expanded
  cat <<EOT
#!/usr/bin/bash --login

# This script updates @ff/ff_agent and restarts it using pm2.
# It was generated by set_environment -> ${FUNCNAME[0]}() on $(date --utc).
EOT
  # Note: this here-doc will NOT be expanded (safe to use '$' without escaping it)
  cat <<'EOT'

state_set "ff_agent_update" 'started'

# Check set_environment is working
set_environment_is_working || {
  # Note" we can not use state_set / abort here.
  echo "ff_agent_update" "set_environment_is_working() failed." 1>&2
  exit 1
}

# Install @ff/ff_agent
npm install --global @ff/ff_agent@${VERSION} || {
  state_set "ff_agent_update" "npm_install_ff_agent_failed"
  abort "${FUNCNAME[0]}" "ff_agent_update" "Failed to npm install @ff/ff_agent@${VERSION}"
}

# Log installed version of @ff/ff_agent
FF_AGENT_VERSION=$(cat ${FF_AGENT_HOME}/.n/lib/node_modules/@ff/ff_agent/package.json | grep '"version"')
log "Installed @ff/ff_agent version: '${FF_AGENT_VERSION}'"

# Flush logs using predefined script
ff_agent_logs_flush || {
  state_set "ff_agent_update" "ff_agent_logs_flush_flush_failed"
  abort "ff_agent_update" "Failed to flush logs"
}

# Restart the service using predefined script
ff_agent_restart || {
  state_set "ff_agent_update" "restart_failed"
  abort "ff_agent_update" "Failed to restart ff_agent"
}

state_set "ff_agent_update" 'success'
EOT
  ) > "${TARGET_FILE}" || {
    state_set "${FUNCNAME[0]}" "failed_to_create_file"
    return 1
  }

  # Make the generated script executable
  chmod a+x "${TARGET_FILE}" || {
    state_set "${FUNCNAME[0]}" "failed_to_chmod_file"
    return 1
  }

  state_set "${FUNCNAME[0]}" 'success'
}
export -f ff_agent_update_install

################################################################################
# CATEGORY
#   SYSTEM SETUP FUNCTIONS
#
# NAME
#   install_authbind - Install and configure authbind for privileged ports
#
# SYNOPSIS
#   install_authbind
#
# DESCRIPTION
#   Installs the `authbind` package and configures port-level access permissions
#   so non-root processes can bind to privileged ports (<=1024), such as 80 and 443.
#
#   This function performs the following steps:
#     - Ensures `authbind` is installed
#     - Ensures configuration files exist for ports 80 and 443 in /etc/authbind/byport/
#     - Applies proper permissions and ownership to those files
#
#   It skips any step that is already completed and is safe to re-run (idempotent).
#
#   Once configured, you can run a binary (or symlink) with elevated bind privileges.
#   Note: the path to the executable can be a symlink.
#     Example:
#       sudo authbind --deep /opt/ff_agent/bin/ff_agent_start
#
#     This grants permission to bind ports like 80/443 without running as root,
#     provided the binary internally performs the binding itself (not via subprocess).
#
# DEPENDENCIES
#   - apt_install
#   - chmod
#   - chown
#   - command_exists
#   - state_set
#
# USAGE EXAMPLE
#   install_authbind
#
# EXIT STATUS
#   Returns 0 on successful install or if already configured
#   Returns 1 on error, setting appropriate state
#
################################################################################
function install_authbind {
  state_set "${FUNCNAME[0]}" 'started'

  # Check dependencies
  local DEPENDENCIES=(
    "apt_install"
    "chmod"
    "chown"
  )
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # Install authbind if missing
  if ! command_exists authbind; then
    apt_install authbind || {
      state_set "${FUNCNAME[0]}" 'failed_to_install_authbind'
      return 1
    }
  fi

  # Define list of ports to configure
  local PORTS=(80 443)
  for PORT in "${PORTS[@]}"; do
    local FILE="/etc/authbind/byport/${PORT}"

    # Create file if it doesn't exist
    if [[ ! -f "${FILE}" ]]; then
      sudo touch "${FILE}" || {
        state_set "${FUNCNAME[0]}" "failed_to_create_authbind_byport_${PORT}"
        return 1
      }
    fi

    # Set restrictive permissions if not already 500
    if [[ "$(stat -c '%a' "${FILE}")" != "500" ]]; then
      sudo chmod 500 "${FILE}" || {
        state_set "${FUNCNAME[0]}" "failed_to_set_permission_authbind_byport_${PORT}"
        return 1
      }
    fi

    # Set ownership to root:root if not already
    if [[ "$(stat -c '%U:%G' "${FILE}")" != "root:root" ]]; then
      sudo chown root:root "${FILE}" || {
        state_set "${FUNCNAME[0]}" "failed_to_set_ownership_authbind_byport_${PORT}"
        return 1
      }
    fi
  done

  state_set "${FUNCNAME[0]}" 'success'
}
export -f install_authbind

################################################################################
#
# Function installs "go" by certain version (see below) if it is not already installed.
#
# In case of successful install (or already at expected version) script return success code 0.
# In case of any errors script prints error to standard error stream and exit with code 1.
#
function install_go {
  state_set "${FUNCNAME[0]}" 'started'

  # Check dependencies
  local DEPENDENCIES=(
    "command_exists"
    "curl"
    "hardware_architecture_get"
    "inject_into_file"
    "is_writable"
    "mktemp"
    "os_name_get"
    "tar"
  )
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # Define required non-empty variables
  local REQUIRED_NON_EMPTY_VARIABLES=(
    "FF_AGENT_HOME"
  )
  ensure_variables_not_empty "${FUNCNAME[0]}" "${REQUIRED_NON_EMPTY_VARIABLES[@]}" || { # Note: ensure_variables_not_empty will report missing variables
    state_set "${FUNCNAME[0]}" 'ensure_variables_not_empty_failed'
    return 1
  }

  # Get the latest available version number. Note: the url below now returns 2 lines,
  # 1st with version (example: go1.21.0)
  # 2nd useless line that breaks it if not supressed (example: time 2023-08-04T20:14:06Z)
  local URL="https://go.dev/VERSION?m=text"
  local EXPECTED_VERSION=$( url_get "${URL}" '-' | head -n1 )

  # Check status of 1st command in the excuted bove pipeline
  if [ ${PIPESTATUS[0]} -ne 0 ]; then
    state_set "${FUNCNAME[0]}" 'failed_to_get_latest_version_number'
    return 1
  fi

  [ ! -z "${EXPECTED_VERSION}" ] || { state_set "${FUNCNAME[0]}" 'failed_to_get_latest_version_number'; return 1; }

  # Check if go is already installed and has expected vesrion
  if command_exists go >/dev/null; then
      # Yes, 'go' is installed. Check if installed version matches expected one.
      INSTALLED_VERSION="$( go version )" || { state_set "${FUNCNAME[0]}" 'failed_to_get_existing_version_number'; return 1; }

      # Compare to expected version
      if [[ "${INSTALLED_VERSION}" =~ ${EXPECTED_VERSION} ]]; then
        # Match. Version is up to date, nothing to do.
        state_set "${FUNCNAME[0]}" 'success'
        return 0
      fi
      # Version mismatch, simply continue installation (to upgrade)...
  fi

  # Create temporary folder (for downloading 'go' archive)
  local TEMP_DIR="$( mktemp --directory )" || { state_set "${FUNCNAME[0]}" 'failed_to_create_temp_dir'; return 1; }

  # Check temporary folder created and we can write to it
  if ! is_writable "${TEMP_DIR}"; then
    state_set "${FUNCNAME[0]}" "error_temp_dir_is_not_writable"
    return 1
  fi

  # Get OS
  local OS="$( os_name_get )" || { state_set "${FUNCNAME[0]}" "failed_to_os_name_get"; return 1; }
  [ ! -z "${OS}" ] || { state_set "${FUNCNAME[0]}" "failed_to_os_name_get"; return 1; }

  # Get ARCHITECTURE
  local ARCHITECTURE="$( hardware_architecture_get )" || { state_set "${FUNCNAME[0]}" "failed_to_hardware_architecture_get"; return 1; }
  [ ! -z "${OS}" ] || { state_set "${FUNCNAME[0]}" "failed_to_hardware_architecture_get"; return 1; }

  # Define archive filename
  TARBALL_FILENAME="${EXPECTED_VERSION}.${OS}-${ARCHITECTURE}.tar.gz"

  # Download archive
  URL="https://go.dev/dl/${TARBALL_FILENAME}"
  curl \
    --silent    \
    --retry 5    \
    --location    \
    --retry-delay 1 \
    --retry-max-time 60 \
    --max-time 185 \
    --connect-timeout 12 \
    -o "${TEMP_DIR}/${TARBALL_FILENAME}" \
    "${URL}" || { state_set "${FUNCNAME[0]}" "failed_to_download_archive"; return 1; }

  # Check if file downloaded (in case of "404 not found" curl return code 0 and will not create "-o file")
  [ -f "${TEMP_DIR}/${TARBALL_FILENAME}" ] || { state_set "${FUNCNAME[0]}" "failed_to_download_archive"; return 1; }

  # Remove previous local version of 'go' if exists
  # 1st "blind assumption" to remove go from expected place
  if [ -d "${FF_AGENT_HOME}/go" ]; then
    rm -fr "${FF_AGENT_HOME}/go" || { state_set "${FUNCNAME[0]}" "failed_to_remove_existing_go"; return 1; }
  fi
  # 2nd remove "go" if it is in the path
  if command_exists go >/dev/null; then
      # Yes, 'go' is installed.
      local EXISTING_GO_FILEPATH=$(command_exists go)
      sudo rm "${EXISTING_GO_FILEPATH}"
  fi
  # Also noted other locations! Must improve "remove go" functionality to cleanup these as well:
  # /usr/bin/go -> ../lib/go-1.13/bin/go
  # /usr/lib/go -> go-1.13/

  # Install 'go'

  # Unzip downloaded archive (the "bin" folder of the golang will be available here: ${FF_AGENT_HOME}/go/bin/ )
  tar -C "${FF_AGENT_HOME}" -xzf "${TEMP_DIR}/${TARBALL_FILENAME}" || { state_set "${FUNCNAME[0]}" "failed_to_extract_archive"; return 1; }

  # Remove temporary folder
  rm -fr "${TEMP_DIR}" || { error "Warning: failed to remove temporary folder: '${TEMP_DIR}'"; }

  # Define the path to ff_agent profile file (we need to add a path to 'go' into ff_agent/.profile if missing)
  TARGET_FILE="${FF_AGENT_HOME}/.profile"

  # Create TARGET_FILE if does not exist and add a comment (who/when creaeted it)
  if [ ! -f "${TARGET_FILE}" ]; then
    (
      cat <<EOT
# File ${TARGET_FILE} created by set_environment ${FUNCNAME[0]}() on $( date --utc ).
EOT
    ) > "${TARGET_FILE}" || { state_set "${FUNCNAME[0]}" "failed_to_create_file"; return 1; }
  fi

  # Check if expected line was already injected into the profile.
  # Note: it is not too specific, it is exactly the line that must be present in the ff_agent custom profille file.
  PATTERN="^export PATH=\${PATH}:${FF_AGENT_HOME}/go/bin"
  INJECT_CONTENT=$(
    cat <<EOT
# The path to 'go' inserted by $( pwd )/$( basename ${0} ) on $( date --utc )'
export PATH=\${PATH}:${FF_AGENT_HOME}/go/bin
EOT
  )
  ERROR_CODE='error_injecting_source_custom_profile'

  # Inject and check result code
  inject_into_file  \
    "${TARGET_FILE}" \
    "${PATTERN}" \
    "${INJECT_CONTENT}" || { state_set "${FUNCNAME[0]}" "${ERROR_CODE}"; return 1; }

  # Also inject path to 'go' into current PATH (if missing in PATH)
  # do the export, so we don't have to relogin in order to call "go version"
  echo ${PATH} | grep --quiet ${FF_AGENT_HOME}/go/bin
  if [ ${?} -ne 0 ]; then
    # Path to 'go' is missing from PATH environment variable. Inject it:
    export PATH=${PATH}:${FF_AGENT_HOME}/go/bin
  fi

  # Check installed 'go' version
  INSTALLED_VERSION="$( go version )" || { state_set "${FUNCNAME[0]}" 'failed_to_get_installed_version_number'; return 1; }

  # Compare to the expected version
  if [[ "${INSTALLED_VERSION}" =~ ${EXPECTED_VERSION} ]]; then
    # Good - expected version installed.
    state_set "${FUNCNAME[0]}" 'success'
    return 0
  else
    # Mismatch.
    state_set "${FUNCNAME[0]}" 'failed_to_match_expected_vs_installed_version'
    return 1
  fi
}
export -f install_go

################################################################################
#
# Installations of "n" based on the tutorial:
#  "Getting started with n (node version management)"
#   https://zacharytodd.com/posts/n-version-manager/
#
#
# Require environment variables set:
#   - FF_AGENT_HOME
#
# Official n github page:
#   https://github.com/tj/n
#   https://github.com/tj/n#installation
#
function install_n {
  state_set "${FUNCNAME[0]}" "start"

  # Check dependencies
  local DEPENDENCIES=(
    "curl"
    "inject_into_file"
    "popd"
    "printenv"
    "pushd"
  )
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # Define required non-empty variables
  local REQUIRED_NON_EMPTY_VARIABLES=(
    "FF_AGENT_HOME"
  )
  ensure_variables_not_empty "${FUNCNAME[0]}" "${REQUIRED_NON_EMPTY_VARIABLES[@]}" || { # Note: ensure_variables_not_empty will report missing variables
    state_set "${FUNCNAME[0]}" 'ensure_variables_not_empty_failed'
    return 1
  }

  # Define path to ff_agent .profile
  local FF_AGENT_PROFILE_FILE="${FF_AGENT_HOME}/.profile"    # example: /home/ubuntu/ff_agent/.profile  (Note: FF_AGENT_PROFILE_FILE is not local, but shell environment used in other install functions)

  # Check custom .profile file exists
  if [ ! -f "${FF_AGENT_PROFILE_FILE}" ]; then
    # Let's not try to re-create it if missing.
    # It should have been created by existing ff_agent_install_bashrc()
  	state_set "${FUNCNAME[0]}" "error_custom_ff_agent_profile_does_not_exist"
    return 1
  fi

  # Create tmp folder and change directory into it
  TMPDIR="$( mktemp -d )"
  pushd "${TMPDIR}" || { state_set "${FUNCNAME[0]}" 'error_pushd_to_tmp_directory'; return 1; }

  # Attempt 1: download 'n' from FF_CONTENT_URL
  # Try FF_CONTENT_URL first because it gives more control over what version of 'n' will be executed.
  # The magour update on the github might introduce some breaking change, let's leave it for 2nd attept only.
  SAVE_AS='n'
  URL="${FF_CONTENT_URL}/ff/ff_agent/hotpatch/hotpatch_files/${SAVE_AS}"
  curl \
    --silent    \
    --retry 5    \
    --location    \
    --retry-delay 1 \
    --retry-max-time 60 \
    --max-time 55 \
    --connect-timeout 12 \
    -o "${SAVE_AS}" \
    "${URL}"

  # Check curl exit code
  if [ ${?} -ne 0 ]; then
      state_set "${FUNCNAME[0]}" 'failed_to_download_n_from_content_server'

      # Attempt 2: download 'n' from from github
      URL="https://raw.githubusercontent.com/tj/n/master/bin/n"
      curl \
        --silent    \
        --retry 5    \
        --location    \
        --retry-delay 1 \
        --retry-max-time 60 \
        --max-time 55 \
        --connect-timeout 12 \
        -o "${SAVE_AS}" \
        "${URL}"

      # Check curl exit code
      if [ ${?} -ne 0 ]; then
        # The 2nd attempt failed too, giving up.
        # Error: clean up tmp folder, report an error and return error code 1
        popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }
        rm -fr "${TMPDIR}" || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
        state_set "${FUNCNAME[0]}" 'warning_failed_to_download_n_from_github'
        return 1
      fi
  fi

  # Export 2 env variables we need for "n" to operate properly:  and NODE_PATH

  # ------------------ Export N_PREFIX and inject that export into FF_AGENT_PROFILE_FILE (begin) ----------------
  export N_PREFIX="${FF_AGENT_HOME}/.n"  # example: /home/ubuntu/ff_agent/.n

  # Add '# Export N_PREFIX' into the custom .profile file if in was not injected earlier.
  TARGET_FILE="${FF_AGENT_PROFILE_FILE}"
  PATTERN="^export N_PREFIX=\"${FF_AGENT_HOME}/.n\""
  EXPECTED_LINE="export N_PREFIX=\"${FF_AGENT_HOME}/.n\""
  INJECT_CONTENT=$(
  cat <<EOT
# N_PREFIX environment injected by set_environment ${FUNCNAME[0]} on $(date --utc)
${EXPECTED_LINE}
EOT
  )
  ERROR_CODE='error_injecting_export_n_prefix_to_ff_agent_profile'

  # Do injection and check result
  inject_into_file  \
    "${TARGET_FILE}" \
    "${PATTERN}" \
    "${INJECT_CONTENT}" || {
      # Error: clean up tmp folder, report an error and return error code 1
      popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }
      rm -fr "${TMPDIR}" || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
      state_set "${FUNCNAME[0]}" "${ERROR_CODE}"
      return 1
  }

  # ------------------ Export N_PREFIX and inject that export into FF_AGENT_PROFILE_FILE (begin) ----------------


  # ------------------ Export NODE_PATH and inject that export into FF_AGENT_PROFILE_FILE (begin) ----------------
  # We set NODE_PATH, so npm can load modules. Details: https://stackoverflow.com/questions/12594541/npm-global-install-cannot-find-module
  export NODE_PATH="${FF_AGENT_HOME}/.n/lib/node_modules"

  # Add '# Export NODE_PATH' into the custom .profile file if in was not injected earlier.
  TARGET_FILE="${FF_AGENT_PROFILE_FILE}"
  PATTERN="^export NODE_PATH=\"${FF_AGENT_HOME}/.n/lib/node_modules\""
  EXPECTED_LINE="export NODE_PATH=\"${FF_AGENT_HOME}/.n/lib/node_modules\""
  INJECT_CONTENT=$(
  cat <<EOT
# Injected by set_environment ${FUNCNAME[0]} on $(date --utc)
${EXPECTED_LINE}
EOT
  )
  ERROR_CODE='error_injecting_export_node_path_to_ff_agent_profile'

  # Do injection and check result
  inject_into_file  \
    "${TARGET_FILE}" \
    "${PATTERN}" \
    "${INJECT_CONTENT}" || {
      # Error: clean up tmp folder, report an error and return error code 1
      popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }
      rm -fr "${TMPDIR}" || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
      state_set "${FUNCNAME[0]}" "${ERROR_CODE}"
      return 1
  }
  # ------------------ Export NODE_PATH and inject that export into FF_AGENT_PROFILE_FILE (end) ----------------


  # ------------------ Inject "ff_agent/.n/bin" into PATH and inject that export into FF_AGENT_PROFILE_FILE (begin) ----------------

  # Update PATH variable (if it not yet contains expected string)
  printenv PATH | grep --quiet "${N_PREFIX}/bin"
  if [ ${?} -ne 0 ]; then
    export PATH="${N_PREFIX}/bin:${PATH}" || {
        # Error: clean up tmp folder, report an error and return error code 1
        popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }; rm -fr "${TMPDIR}"  || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
        state_set "${FUNCNAME[0]}" 'error_modifying_path'; return 1;
    }
  fi

  # Note: we escape "\$" in front of the PATH, so it does not get expanded
  TARGET_FILE="${FF_AGENT_PROFILE_FILE}"
  PATTERN="^export PATH=\"${N_PREFIX}/bin:\${PATH}\""
  EXPECTED_LINE="export PATH=\"${N_PREFIX}/bin:\${PATH}\""
  if ! file_contains_pattern "${TARGET_FILE}" "${PATTERN}"; then
    # Expected line is missing, Inject text
    (
      cat <<EOT
# Injected by set_environment ${FUNCNAME[0]} on $(date --utc)
${EXPECTED_LINE}
EOT
    ) >> "${TARGET_FILE}" || {
        # Error: clean up tmp folder, report an error and return error code 1
        popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }; rm -fr "${TMPDIR}"  || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
        state_set "${FUNCNAME[0]}" 'error_injecting_n_prefix_bin_to_ff_agent_profile'; return 1;
    }
  fi
  # ------------------ Inject "ff_agent/.n/bin" into PATH and inject that export into FF_AGENT_PROFILE_FILE (end) ----------------

  #
  VERSION=$( nodejs_desired_version_get )
  [ -z "${VERSION}" ] && { state_set "${FUNCNAME[0]}" 'failed_to_nodejs_desired_version_get'; abort 'failed_to_nodejs_desired_version_get'; }

  # Install 'n_lts' using dowloaded into TMPDIR 'n' ('n_lts' will be installed into ff_agent/.n)
  retry_command 5 15 bash n "${VERSION}" || {
    # Error: clean up tmp folder, report an error and return error code 1
    popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }; rm -fr "${TMPDIR}"  || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
    state_set "${FUNCNAME[0]}" 'error_installing_n'; return 1;
  }

  # Install 'n' into ff_agent/.n  (yes, we have downloaded 'n' into TMPDIR,
  # then used it to install 'npm', and now we use 'npm' to install "globally" n.
  # The proper installed 'n' will reisde under ff_agent/.n/ folder)
  npm install --verbose --global n || {
    # Error: clean up tmp folder, report an error and return error code 1
    popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }; rm -fr "${TMPDIR}"  || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }
    state_set "${FUNCNAME[0]}" 'error_installing_n'; return 1;
  }

  # Clean up tmp folder
  popd || { state_set "${FUNCNAME[0]}" 'error_popd'; return 1; }; rm -fr "${TMPDIR}"  || { state_set "${FUNCNAME[0]}" 'failed_to_remove_tmpdir'; return 1; }

  # Chage state and return success
  state_set "${FUNCNAME[0]}" 'success'
}
export -f install_n

################################################################################
#
function install_nodejs {
  state_set "${FUNCNAME[0]}" 'started'

 # Check dependencies
  local DEPENDENCIES=(
    "install_n"
  )
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # Get desired nodejs version
  local VERSION="$( nodejs_desired_version_get )"
  [ -z "${VERSION}" ] && { state_set "${FUNCNAME[0]}" 'failed_to_nodejs_desired_version_get'; abort 'failed_to_nodejs_desired_version_get'; }


  # # We need to stop pm2 before replacing location of nodejs, otherwise any pm2 command would faild
  # stop_pm2
  # uninstall_n_outside_ff_agent_home  || { state_set "${FUNCNAME[0]}" 'terminal_error_uninstall_n_outside_ff_agent_home'; abort 'terminal_error_uninstall_n_outside_ff_agent_home'; }
  install_n || { state_set "${FUNCNAME[0]}" 'terminal_error_install_n'; abort 'terminal_error_install_n'; }
  n install "${VERSION}" || { state_set "${FUNCNAME[0]}" 'terminal_error_switching_node_version'; abort 'terminal_error_switching_node_version'; }

  state_set "${FUNCNAME[0]}" 'success'
}
export -f install_nodejs

################################################################################
#
# Install nodejs latest environment
function install_nodejs_suite {
  state_set "${FUNCNAME[0]}" 'started'

 # Check dependencies
  local DEPENDENCIES=(
    "install_authbind"
    "install_nodejs"
  )
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # We want nodejs to be able to open ports 443 and 80, so we will install_authbind before this.

  install_authbind || { state_set "${FUNCNAME[0]}" 'install_authbind_failed'; abort 'install_authbind_failed'; }

  install_nodejs || { state_set "${FUNCNAME[0]}" 'terminal_error_install_node'; abort 'terminal_error_install_node'; }

  state_set "${FUNCNAME[0]}" 'success'
}
export -f install_nodejs_suite

################################################################################
#
# Continuation of the "set_environment". Installing baseline components.
# On error: function aborts (so no need to errorcheck on caller side)
#
function install_set_environment_baseline {
  state_set "${FUNCNAME[0]}" 'started'

 # Check dependencies
  local DEPENDENCIES=(
    "abort"
    "assert_clean_exit"
    "discover_environment"
    "set_environment_ensure_ff_agent_bin_exists"
    "setup_logging"
  )
  check_dependencies "${FUNCNAME[0]}" "${DEPENDENCIES[@]}" || {  # Note: check_dependencies will report missing dependencies
    state_set "${FUNCNAME[0]}" 'dependencies_check_failed'
    return 1
  }

  # Discover environment (choose user, make sure it's home folder exists, check FF_CONTENT_URL is set etc.)
  discover_environment || abort "${FUNCNAME[0]}" "failed to discover_environment"

  # Now we can state_set()
  state_set "${FUNCNAME[0]}" 'started'

  # Put logs in best location
  setup_logging || { state_set "${FUNCNAME[0]}" "terminal_error_failed_to_setup_logging"; abort 'terminal_error_failed_to_setup_logging'; }

  # Ensure ff_agent/bin folder exists before assert_baseline_components
  set_environment_ensure_ff_agent_bin_exists || { state_set "${FUNCNAME[0]}" "terminal_error_failed_to_set_environment_ensure_ff_agent_bin_exists"; abort 'terminal_error_failed_to_set_environment_ensure_ff_agent_bin_exists'; }

  # Install set of basic packages, bash functions, .bashrc and .profile files
  assert_clean_exit assert_baseline_components || { state_set "${FUNCNAME[0]}" "terminal_error_failed_to_assert_baseline_components"; abort 'terminal_error_failed_to_assert_baseline_components'; }

  state_set "${FUNCNAME[0]}" 'success'
}
export -f install_set_environment_baseline

